string convert10tob(l N)
{

     if (N == 0)
        return "";

     l x = N % 3;

     N /= 3;

     if (x < 0)
        N += 1; 

     convert10tob(N);

     l a = x < 0 ? x + (3 * -1) : x;
     if(a==0)
     {
         s1=s1+'0';
     }
     else if(a==1)
     {
         s1=s1+'1';
     }
     else if(a==2)
     {
         s1=s1+'2';
     }


     return s1;
}

