            {
                cnt=cnt*3;
                if(con[csi]=='0')
                {
                    csi--;
                    continue;
                }
                if(con[csi]=='1')
                {
                   second+=(cnt*2);
                }
                else if(con[csi]=='2')
                {
                    second+=(cnt*1);
                }
                csi--;
            }
            arr[second]=i;
            arr[i]=second;
        }
    }

    l arr1[total];
    for(l i=0;i<total;i++)
    {
        arr1[i]=i;
    }
    l si=s.length();
    l t=total-1;
    for(l i=0;i<si;i++)
    {
        if(i==0 || i==(si-1))
        {
            if(s[i]=='S')
            {
                for(l j=0;j<total;j++)
                {
                    arr1[j]=arr[arr1[j]];
                }
            }
            else{
                for(l j=0;j<total;j++)
                {
                    if(arr1[j]!=t)
                    {
                        arr1[j]+=1;
                    }
                    else{
                        arr1[j]=0;
                    }
                }
            }
        }
        else
        {
            if(s[i]=='R')
            {
                for(l j=0;j<total;j++)
                {
                    if(arr1[j]!=t)
                    {
                        arr1[j]+=1;
                    }
                    else{
                        arr1[j]=0;
                    }
                }
            }
