    for (int i=0; i<N; i++) {
        for (int k=0; k<K; k++) {
            int d = (i/p3[k])%3;
            if (d == 1)      f[i] += p3[k]*2;
            else if (d == 2) f[i] += p3[k];
        }
    }

}

