void brute(int k, string s) {
    int n = p3[k];
    for (int i=0; i<n; i++) aa[i] = i;

    for (char c: s) {
        if (c == 'R') {
            int last = aa[n-1];
            for (int i=n-1; i>0; i--)     aa[i] = aa[i-1];
            aa[0] = last;
        }
        else {
            for (int i=0; i<n; i++)
                if (f[i] > i)
                    swap(aa[i], aa[f[i]]);
        }
    }

    for (int i=0; i<n; i++) bb[aa[i]] = i;
    for (int i=0; i<n; i++) cout<<bb[i]<<" ";
