};

vector<Node> S(1);

int create_node() {
	S.pb({0, 0, 0, 0});
	return le(S) - 1;
}
int rev(int i) {
	vector<int> digs;
	f(t, n) {
		digs.pb(i % 3);
		i /= 3;
	}
	int rez = 0;
	for (int x : digs) {
		rez = rez * 3 + x;
	}
	return rez;
}
int iter = 0;
int init(int depth) {
	int i = create_node();
	if (depth == 0) {
		S[i].id = iter; 
		iter++;
	} else {
		for (int j = 0; j < 3; j++) {
			int t = init(depth - 1);
			S[i].go[j] = t;
		}
	}
	return i;
}

void push(int i, int depth) {
	assert(depth);
	if (S[i].mod) {
		swap(S[i].go[1], S[i].go[2]);
		f(t, 3) {
			S[S[i].go[t]].mod ^= 1;
		}
		S[i].mod = 0;
	}
}

void inc(int i, int depth) {
	if (depth) {
		push(i, depth);
		// rotate(S[i].go, S[i].go + 1, S[i].go + 3);
		// reverse(S[i].go, S[i].go + 3);
		tie(S[i].go[0], S[i].go[1], S[i].go[2])
			= mt(S[i].go[2], S[i].go[0], S[i].go[1]);
		inc(S[i].go[0], depth - 1);
	}
}

void extract(int i, int depth, vector<int> &rez) {
	if (!depth) {
		rez.pb(S[i].id);
	} else {
		push(i, depth);
		f(t, 3) {
			extract(S[i].go[t], depth - 1, rez);
		}
	}
}

