void Update(void)
{
  rep(i,pw3[N-N/2])
  {
    y=i;
    rep(j,pos){if(sw[j+1]>0)y=salsa[y];else if(sw[j+1]<0){if((++y)==pw3[N-N/2])y=0;}}
    A[o+i*pw3[N/2]]=y*pw3[N/2]+x;
  }
}
