int tot,n,pw[20],ch[N][3],tag[N],pos[N],ans[N],rt,m;
char s[N];

void build(int &id,int dep,int now)
{
    id=(++tot);
    if (dep==n)
    {
        pos[id]=now;return;
    }
    build(ch[id][0],dep+1,now);
    build(ch[id][1],dep+1,now+pw[dep]);
    build(ch[id][2],dep+1,now+pw[dep]*2);
}

void addtag(int id) 
{
    tag[id]^=1;
    swap(ch[id][1],ch[id][2]);
}

void pushdown(int id)
{
    if (tag[id])
    {
        addtag(ch[id][0]);addtag(ch[id][1]);addtag(ch[id][2]);
        tag[id]=0;
    }
}

void add(int id,int dep)
{
    if (dep==n) return;
    pushdown(id);
    int x=ch[id][0],y=ch[id][1],z=ch[id][2];
    ch[id][0]=z;ch[id][1]=x;ch[id][2]=y;
    add(ch[id][0],dep+1);
}

void findans(int id,int dep,int now)
{
    if (dep==n)
    {
        ans[pos[id]]=now;return;
    }
    pushdown(id);
    findans(ch[id][0],dep+1,now);
    findans(ch[id][1],dep+1,now+pw[dep]);
    findans(ch[id][2],dep+1,now+pw[dep]*2);
}

int main()
{
    n=read();
    pw[0]=1;
    rep(i,1,n) pw[i]=pw[i-1]*3;
    build(rt,0,0);
    scanf("%s",s+1);m=strlen(s+1);
    rep(i,1,m)
    {
        if (s[i]=='S') addtag(rt);
        else add(rt,0);
    }
    findans(rt,0,0);
    rep(i,0,pw[n]-1) printf("%d ",ans[i]);
    return 0;
}

