bool is_prime(ll n)
{
	bool flg = true;
	if (n <= 1)
		flg = false;
	else if (n == 2)
		flg = true;
	else if (n % 2 == 0)
		flg = false;
	else
	{
		for (ll i = 3; i * i <= n; i += 2)
		{
			if (n % i == 0)
				flg = false;
		}
	}
	return flg;
}

