// 
vector<string> split(string s, string delim)
{
	if (s.empty())
		return {};
	if (delim.empty())
		return { s };
	int start = 0;
	auto delim_pos = s.find(delim);
	vector<string> ret_v;
	while (delim_pos != string::npos)
	{
		if (start != delim_pos)
			ret_v.push_back(s.substr(start, delim_pos - start)); // vector
		start = delim_pos + delim.size();
		delim_pos = s.find(delim, start);
	}
	if (start < s.size())
		ret_v.push_back(s.substr(start)); // 
	return ret_v;
}

