  auto update=[&](int i,int j,int val){
    W[i][j%W[i].size()]=(flip?3-val:val)%3;
  };

  auto rumba=[&](){
    int s=0,d=1;
    for(int i=0;i<n;i++){
      int x=(query(i,s+d*0)+1)%3;
      int y=(query(i,s+d*1)+1)%3;
      int z=(query(i,s+d*2)+1)%3;
      update(i,s+d*0,x);
      update(i,s+d*1,y);
      update(i,s+d*2,z);
      if(x==0) s+=d*0;
      if(y==0) s+=d*1;
