char s[222222];
void build(int &nw,int c,int dep,int sum)
{
	nw=++sz;
	if (dep==n)
	{
		val[nw]=sum;
		return;
	}
	build(son[nw][0],c*3,dep+1,sum);
	build(son[nw][1],c*3,dep+1,sum+c);
	build(son[nw][2],c*3,dep+1,sum+2*c);
}
void push(int node)
{
	swap(son[node][1],son[node][2]);
	tag[node]^=1;
}
void pushdown(int node)
{
	if (tag[node])
	{
		push(son[node][0]);
		push(son[node][1]);
		push(son[node][2]);
		tag[node]=0;
	}
}
void go(int nw,int dep)
{
	if (dep==n) return;
	pushdown(nw);int s1=son[nw][0],s2=son[nw][1],s3=son[nw][2];
	son[nw][0]=s3;son[nw][1]=s1;son[nw][2]=s2;
	go(son[nw][0],dep+1);
}
void cal(int nw,int c,int dep,int sum)
{
	if (dep==n)
	{
		ans[val[nw]]=sum;
		return;
	}
	pushdown(nw);
	cal(son[nw][0],c*3,dep+1,sum);
	cal(son[nw][1],c*3,dep+1,sum+c);
	cal(son[nw][2],c*3,dep+1,sum+2*c);
}
