		}
		else
		{
			go(rt,0);
		}
	}
	cal(rt,1,0,0);
	all=1;
	for (int i=1;i<=n;i++) all*=3;
	for (int i=0;i<all;i++) printf("%d ",ans[i]);
	printf("\n");
	return 0;
}

