const int maxn = 2000010;
int n, ans[maxn];
char s[maxn];

namespace TRIE {
int tot = 1, num[maxn], ch[maxn][3], tag[maxn];

void pushdown(int k) {
    swap(ch[k][1], ch[k][2]), tag[k] = 0;
    for (int i = 0; i < 3; i++) tag[ch[k][i]] ^= 1;
}

void ins(int x) {
    int cur = 1, y = x;
    for (int i = n; i; i--, x /= 3) {
        int c = x % 3;
        if (!ch[cur][c]) ch[cur][c] = ++tot;
        cur = ch[cur][c];
    }
    num[cur] = y + 1;
}

void inc() {
    int cur = 1;
    for (int i = 1; i <= n; i++) {
        if (tag[cur]) pushdown(cur);
        swap(ch[cur][0], ch[cur][2]), swap(ch[cur][1], ch[cur][2]);
        cur = ch[cur][0];
    }
}

void dfs(int k, int pos = 0, int w = 1) {
    if (tag[k]) pushdown(k);
    if (num[k]) { ans[num[k] - 1] = pos; return; }
    for (int i = 0; i < 3; i++) dfs(ch[k][i], pos + i * w, w * 3);
}
}  // namespace TRIE

int main() {
    scanf("%d %s", &n, s + 1);
    int tot = pow(3, n);
    for (int i = 0; i < tot; i++) {
        TRIE::ins(i);
    }
    for (int i = 1, l = strlen(s + 1); i <= l; i++) {
        if (s[i] == 'S') TRIE::tag[1] ^= 1;
        else TRIE::inc();
    }
    TRIE::dfs(1);
    for (int i = 0; i < tot; i++) {
        printf("%d ", ans[i]);
    }
    return 0;
}

