string to_ter(int x, int k=-1) {
    string res;
    while(x) {
        res+='0'+x%3;
        x/=3;
    }
    if(k>=0) {
        while(res.size()<k) res+='0';
    }
    return res;
}
int to_int(const string & s) {
    int res=0;
    // rrep(i,s.size()-1,-1)
