		}

		fin[i][j + 1] = fin[i - 1][j + 1] + m * a[2][fin[i - 1][j + 1]];
	}

	forn (j, 3 * m) p[i][j] = p[i - 1][j % m] + m * a[j / m][p[i - 1][j % m]];
}

