int to[maxn][N]; 

int get(int l, int r) {
    return pref[r + 1] - pref[l]; 
}

void change(int &mask, int l, int r) {
    if (get(l, r) % 2) {
        mask = get(mask, 'S'); 
    }    
    mask++; 
}

int merge(int left, int right) {
    for (int i = 0; i < 7; ++i) right *= 3;
    return left + right;     
}

int get(int x) {
    vector<int> bts; 
    for (int i = 0; i < L; ++i) {
        bts.pb(x % 3); 
        x /= 3; 
    }
    int left = 0;
    int right = 0; 
    for (int i = 6; i >= 0; --i) {
        left *= 3;
        left += bts[i]; 
    }
    for (int i = L - 1; i >= 7; --i) {
        right *= 3; 
        right += bts[i]; 
    }
    int id = 0; 
    if (left && dp1[left] == -1) {
        return merge(to[0][left], right);  
    }
    if (left) {    
        id = dp1[left]; 
        change(right, 0, id - 1);             
        left = 0; 
    }                     
    while (next[id] != -1) {
        change(right, id, next[id] - 1);
        id = next[id];                                                                                                                                         
    }
    left = to[id][0]; 
    return merge(left, right); 
}


