        for(auto c:tmp){
            if(!t.empty() && t.back()=='S' && c=='S') t.pop_back();
            else t+=c;
        }
    }

    vector<vector<int>> memo(1<<SZ);

    for(int bit=0;bit<(1<<SZ);bit++){
        bool ispass= false;
        for(int j=0;j+1<SZ;j++){
            if(((bit>>j)&1)==0 && ((bit>>(j+1)))==0) ispass=true;
        }
        if(ispass) continue;

        memo[bit].resize(pow3n);
        iota(memo[bit].begin(),memo[bit].end(),0);
        for(int j=0;j<SZ;j++){
            if(((bit>>j)&1)==0) do_s(memo[bit]);
            else do_r(memo[bit]);
        }
    }
    vector<int> res(pow3n);
    iota(res.begin(),res.end(),0);
    int l = 0;
    while(t.size()-l>=SZ){
        int r;
        for(r=l;r<t.size();r++){
            if(t[r]=='S') break;
        }
        if(r-l>=SZ){
            do_rx(res,r-l);
            l=r;
        }
        else{
            int bit = 0;
            for(int j=0;j<SZ;j++){
                if(t[l+j]=='R') bit+=(1<<j);
            }
            perm(res,memo[bit]);
            bit=0;
            l+=SZ;
        }
    }

    for(int i=l;i<t.size();i++){
        // cerr<<"#"<<endl;
        if(t[i]=='S') do_s(res);
        else do_r(res);
    }
