int conv(int inp) {
  int cv=0;
  int t;
  for (t=1; inp>0; t*=3) {
    switch(inp % 3) {
    case 1: cv += 2*t; break;
    case 2: cv += t; break;
    }
    inp /= 3;
  }
  return cv;
}

void salsa() {
  int i;

  if (sp==1) {
    for (i=0; i<tn; i++) {
      p2[conv(i)] = p1[i];
    }
    sp = 2;
  } else {
    for (i=0; i<tn; i++) {
      p1[conv(i)] = p2[i];
    }
    sp = 1;
  }
}
void rumba() {
  int i;
  if (sp==1) {
    for (i=0; i<tn-1; i++) {
      p2[i+1]=p1[i];
    }
    p2[0] = p1[tn-1];
    sp = 2;
  } else {
    for (i=0; i<tn-1; i++) {
      p1[i+1]=p2[i];
    }
    p1[0] = p2[tn-1];
    sp = 1;
  }
}

