using namespace std;

const int maxn = 8e5;
int n, m, tot, val[maxn + 5], ch[maxn + 5][3], cnt, res[maxn + 5];
bool sal[maxn + 5];
char s[maxn + 5];

int dfs(int k, int t, int s) {
	int x = ++tot;
	if (!k) return val[x] = s, x;
	ch[x][0] = dfs(k - 1, t * 3, s);
	ch[x][1] = dfs(k - 1, t * 3, s + t);
	ch[x][2] = dfs(k - 1, t * 3, s + t * 2);
	return x;
}

void salsa(int x) {
	sal[x] ^= 1, swap(ch[x][1], ch[x][2]);
}

void push_down(int x) {
	if (sal[x]) {
		rep(i, 0, 2) salsa(ch[x][i]);
		sal[x] = false;
	}
}

void rumba(int x) {
	if (!ch[x][0]) return;
	push_down(x);
	int t = ch[x][0];
	ch[x][0] = ch[x][2];
	ch[x][2] = ch[x][1];
	ch[x][1] = t;
	rumba(ch[x][0]);
}

void work(int x, int t, int s) {
	if (!ch[x][0]) {
		res[val[x]] = s, cnt++;
		return;
	}
	push_down(x);
	rep(i, 0, 2) work(ch[x][i], t * 3, s + i * t);
}

