	scanf("%d %s", &n, s + 1);
	m = strlen(s + 1);
	dfs(n, 1, 0);
	rep(i, 1, m) {
		if (s[i] == 'S') salsa(1);
		else rumba(1);
	}
	work(1, 1, 0);
	rep(i, 0, cnt - 1) printf("%d%c", res[i], " \n"[i == cnt - 1]);
	return 0;
}

