int dp1[N]; 
int dp2[N]; 
int xyz[N]; 

int get1(int mask, char q) {
    if (q == 'R') {
        ++mask; 
        return mask; 
    }   
    int ptr = 1; 
    int ans = 0; 
    while (mask) {
        int t = mask % 3; 
        mask /= 3; 
        if (t == 2) ans += ptr; 
        if (t == 1) ans += 2 * ptr; 
        ptr *= 3; 
    }
    return ans; 
}

const int L = 12; 
int pref[maxn]; 
int to[maxn]; 
int kek1[N], kek2[N]; 
int res[N], res1[N]; 
int add = 1; 

int get(int x) {
    vector<int> bts; 
    for (int i = 0; i < L; ++i) {
        bts.pb(x % 3); 
        x /= 3; 
    }
    int left = 0;
    int right = 0; 
    for (int i = 6; i >= 0; --i) {
        left *= 3;
        left += bts[i]; 
    }
    for (int i = L - 1; i >= 7; --i) {
        right *= 3; 
        right += bts[i]; 
    }
    int id = 0;         
    if (left && dp1[left] == -1) {
        if (pref[(int)s.size()] & 1) right = xyz[right];   
        return res[left] + add * right;   
    }
    if (left) {    
        id = dp1[left]; 
        if (pref[id] & 1) right = xyz[right];             
        left = 0; 
    }                     
    while (next[id] != -1) {        
        if ((pref[next[id]] - pref[id]) & 1) right = xyz[right]; 
        id = next[id];                                                                                                                                         
    }
    if ((pref[(int)s.size()] - pref[id]) & 1) right = xyz[right]; 
    left = to[id]; 
    return left + right * add; 
}

void solve() {
    for (int i = 0; i < N; ++i) {
        xyz[i] = get1(i, 'S') % N; 
    }
    cin >> n >> s; 
    int t = s.size(); 
    for (int i = 0; i < t; ++i) {
        pref[i + 1] = pref[i] + (s[i] == 'S'); 
    }

    for (int i = 0; i < N; ++i) dp1[i] = dp2[i] = -1; 
    next[t] = -1;
    for (int i = 0; i < N; ++i) kek1[i] = i;              
    for (int i = 0; i < N; ++i) res[i] = i; 
    for (int i = t - 1; i >= 0; --i) {
        for (int mask = 0; mask < N; ++mask) {
            int mask1; 
            if (s[i] == 'S') mask1 = xyz[mask]; 
            else mask1 = mask + 1; 
            if (mask1 >= N) mask1 -= N; 
            res1[mask] = res[mask1]; 
            kek2[mask] = kek1[mask1];
            if (mask && mask1 == 0) {
                dp2[mask] = i + 1; 
            } else {
                dp2[mask] = dp1[mask1]; 
            }                                                                                 
        }
        to[i] = kek2[0]; 
        next[i] = dp2[0]; 
        for (int mask = 0; mask < N; ++mask) {
            res[mask] = res1[mask]; 
            kek1[mask] = kek2[mask]; 
            dp1[mask] = dp2[mask]; 
            dp2[mask] = -1; 
        }
    }
    for (int i = 0; i < 7; ++i) add *= 3; 
    int tet = 1; 
    for (int i = 0; i < n; ++i) tet *= 3; 
    for (int ptr = tet - 1; ptr < tet; ++ptr) {
        cout << get(ptr) % tet << " "; 
    }
//    cout << TIME; 
    return; 
}                                

