int carry[200005];
int dest[600005], ndest[600005];
set<int> S[3];
int pt[3];

int main(void)
{
	ios::sync_with_stdio(0);
	cin.tie(0);

	cin >> n;
	cin >> s;

	beki[0] = 1;
	for(int i = 1; i < 15; i++) beki[i] = beki[i-1] * 3;

	for(int i = 0; i < 3; i++){
		int p = i;
		for(int j = 0; j < s.size(); j++){
			if(s[j] == 'R'){
				if(p == 2) carry[j] = i;
				p = (p+1) % 3;
			}
			else p = (3-p)%3;
		}
		dest[i] = p;
	}

	for(int i = 1; i < n; i++){
		for(int j = 0; j < 3; j++){
			S[j].clear();
			for(int k = 0; k < beki[i]; k++){
				S[j].insert(j*beki[i]+k);
			}
		}
		for(int j = 0; j < 3; j++) pt[j] = j;
		for(int j = 0; j < s.size(); j++){
			if(s[j] == 'S') swap(pt[1], pt[2]);
			else{
				int tmp[3];
				for(int k = 0; k < 3; k++){
					for(int l = 0; l < 3; l++){
						int x = l*beki[i]+carry[j];
						if(S[pt[k]].count(x)){
							tmp[k] = x;
							if(k == 2) carry[j] = x;
							break;
						}
					}
				}
				for(int k = 0; k < 3; k++){
					S[pt[k]].erase(tmp[k]);
					S[pt[(k+1)%3]].insert(tmp[k]);
				}
			}
		}
		for(int j = 0; j < 3; j++){
			for(auto it = S[pt[j]].begin(); it != S[pt[j]].end(); it++){
				ndest[*it] = dest[*it%beki[i]] + j*beki[i];
			}
		}
		for(int j = 0; j < beki[i+1]; j++) dest[j] = ndest[j];
	}

	for(int i = 0; i < beki[n]; i++) cout << dest[i] << " "; cout << endl;

	return 0;
}

