template <class T, class Op = multiplies<T>>
constexpr T power(T a, long long n, Op op = Op(), T e = {1}) {
  assert(n >= 0);
  while (n) {
    if (n & 1) e = op(e, a);
    if (n >>= 1) a = op(a, a);
  }
  return e;
}

int f(int n) {
  int res = 0, b = 1;
  while (n) {
    int r = n % 3;
    res += (r ? 3 - r : 0) * b;
    b *= 3;
    n /= 3;
  }
  return res;
}

vector<int> solve(int n, string s) {
  reverse(begin(s), end(s));
  int m = power(3, n);
  vector<int> p(m, -1);
  auto to = [&](int i) {
    for (char c : s) {
      if (c == 'S') {
        i = f(i);
      } else {
        i = (i + m - 1) % m;
      }
    }
    return i;
  };
  p[0] = to(0);
  for (int k = 0; k < n; ++k) {
    p[power(3, k)] = to(power(3, k));
    p[2 * power(3, k)] = to(2 * power(3, k));
  }
  for (int i = 0; i < m; ++i) {
    if (p[i] != -1) continue;
    // p[i] = to(i);
    // continue;
    p[i] = i;
    int cur = i;
    for (int k = n; k--; ) {
      if (cur >= 2 * power(3, k)) {
        cur -= 2 * power(3, k);
        p[i] = p[cur] + p[2 * power(3, k)] - p[0] + m;
        p[i] %= m;
        break;
      } else if (cur >= power(3, k)) {
        p[i] = p[cur] + p[power(3, k)] - p[0] + m;
        p[i] %= m;
        cur -= power(3, k);
        break;
      }
    }
  }
  return p;
  vector<int> res(m);
  for (int i = 0; i < m; ++i) {
    res[p[i]] = i;
  }
  return res;
}

