struct S{
	int x;
	bool fl;
	S* g[3];
	S():fl(false) { f(i, 0, 3)g[i] = 0; }
}*R;

void pl(S* &v = R, int m = 0, int x = 0){
	v = new S();
	if (m == n){
		v->x = x;
		return;
	}
	pl(v->g[0], m + 1, x);
	pl(v->g[1], m + 1, x + p3[m]);
	pl(v->g[2], m + 1, x + 2 * p3[m]);
}

void flp(S* v = R){
	swap(v->g[1], v->g[2]);
	v->fl ^= 1;
}

void sl(S* v){
	if (!v->fl)return;
	v->fl = false;
	flp(v->g[0]);
	flp(v->g[1]);
	flp(v->g[2]);
}

void ad(S* v = R){
	if (!v->g[0])return;
	sl(v);
	S* t = v->g[2];
	v->g[2] = v->g[1];
	v->g[1] = v->g[0];
	v->g[0] = t;
	ad(t);
}

void fn(S* v = R, int m = 0, int x = 0){
	if (m == n){
		an[v->x] = x;
		return;
	}
	fn(v->g[0], m + 1, x);
	fn(v->g[1], m + 1, x + p3[m]);
	fn(v->g[2], m + 1, x + 2 * p3[m]);
}

