bool add(string & x, const string & y) {
    int n1=x.size(), n2=y.size(),carry=0;
    rep(i,0,n1) {
        if(i<n1) carry+=x[i]-'0';
        if(i<n2) carry+=y[i]-'0';
        x[i]=carry%3+'0';
        carry/=3;
    }
    return carry==0;
}

vi cal_inst(string & s) {
    int n=s.size();
    vi res(1);
    int i=0;
    while(i<n) {
        if(s[i]=='R') {
            res.back()++;
            i++;
        }
        else {
            int j=i;
            for(;j<n&&s[j]=='S';j++);
            if((j-i)%2) res.pb(0);
            i=j;
        }
    }
    if(s.back()=='S') res.pop_back();
    return res;
}

char rev(char c) {
    if(c>'0') return '0'+(3-(c-'0'));
    return c;
}
string rev(string s) {
    for(auto & c:s) c=rev(c);
    return s;
}

int len(string & s) {
    int res=s.size()-1;
    while(res>=0&&s[res]=='0') res--;
    return res+1;
}

int power[13]={};
int revmap[MAXN]={};
int tmpres[MAXN];
int rev(int x) {
    for(int flag=1;flag<power[12];flag*=3) {
        int tmp=x/flag%3;
        if(tmp==1) x+=flag;
        else if(tmp==2) x-=flag;
    }
    return x;
}

void cal(vi inst, int n, int tot) {
    // vi tmpres(tot,-1);
    fill(tmpres,tmpres+tot,-1);

    rep(i,0,tot) {
        if(tmpres[i]>=0) continue;

        int cur=i,ok=1;
        for(auto x:inst) {
            cur+=x;
            if(cur>=power[n]) {
                ok=0; 
                cur%=power[n];
            }
            cur=revmap[cur];
        }
        // if(i<=9) print(i,cur);
        tmpres[i]=cur;
        // assert(cur>=0);
        if(!ok) continue;
        int flag=1,tmp;
        for(;flag<=i||flag<=cur;flag*=3);

        if(inst.size()%2==0) {
            for(tmp=flag;tmp<power[n];tmp+=flag) {
                tmpres[tmp+i]=tmp+cur;
            }
        }
        else {
            for(tmp=flag;tmp<power[n];tmp+=flag) {
                tmpres[tmp+i]=revmap[tmp]+cur;
            }

        }
    }
    // print(1);
    // print(*max_element(all(res)),*min_element(all(res)));
    // return res;
}

