using namespace std;
typedef long long ll;
const int MAXN = 12 + 5;
const int MAXM = 2e5 + 5;
const int MAXP = 1e6 + 5;

int pw3[MAXN];

struct Node
{
	int id;
	bool tag;
	Node* son[3];

	inline void push_down(void)
	{
		if(!tag) return;
		tag=0;
		swap(son[1],son[2]);
		for(int i=0; i<3; ++i)
			son[i] -> tag ^= 1;
	}
}p[MAXP];
int pcnt=0;

int n;

inline void build(Node *&u,int dep,int now)
{
	u = &p[++pcnt];
	if(dep==n)
	{
		u -> id = now;
		return;
	}
	for(int i=0; i<3; ++i)
		build(u -> son[i], dep+1, now + i*pw3[dep]);
}

inline void add(Node *u,int dep)
{
	if(dep==n) return;
	u -> push_down();

	Node *tmp = u -> son[2];
	u -> son[2] = u -> son[1];
	u -> son[1] = u -> son[0];
	u -> son[0] = tmp;

	add(u -> son[0], dep+1);
}

int ans[MAXP];

inline void dfs(Node *u,int dep,int now)
{
	if(dep==n)
	{
		ans[u -> id] = now;
		return;
	}
	u -> push_down();
	for(int i=0; i<3; ++i)
		dfs(u -> son[i], dep+1, now + i*pw3[dep]);
}

int m;
char s[MAXM];

int main(void)
{
	scanf("%d%s",&n,s+1);
	m = strlen(s+1);

	pw3[0]=1;
	for(int i=1; i<=n; ++i) pw3[i] = pw3[i-1]*3;

	Node *root;
	build(root,0,0);

	for(int i=1; i<=m; ++i)
	{
		if(s[i]=='S')
			root -> tag ^= 1;
		else add(root,0);
	}

	dfs(root,0,0);
	for(int i=0; i<pw3[n]; ++i)
		printf("%d ",ans[i]);
	return 0;
}

#include<bits/stdc++.h>
#define cs const
#define pb push_back
using namespace std;
cs int N = 6e5 + 50;
int n, p[N]; char S[N];
int ch[N][3], tag[N], vl[N], nd, pw[20];
int build(int d, int v){
	if(!d) return vl[++nd]=v, nd; int x=++nd;
	ch[x][0]=build(d-1, v);
	ch[x][1]=build(d-1, v + pw[n-d]);
	ch[x][2]=build(d-1, v + 2 * pw[n-d]); return x;
}
void put(int x){ if(!x) return; tag[x] ^= 1; swap(ch[x][1],ch[x][2]); }
void down(int x){ if(tag[x]) put(ch[x][0]),put(ch[x][1]),put(ch[x][2]),tag[x]=0; }
void work(int x, int d){
	if(!d) return;
	down(x); int v = ch[x][2]; ch[x][2] = ch[x][1]; 
	ch[x][1] = ch[x][0]; ch[x][0] = v; 
	work(v,d-1);
}
void dfs(int x, int d, int v=0){
	if(!d) return p[vl[x]] = v, void();
	down(x); dfs(ch[x][0],d-1,v);
	dfs(ch[x][1],d-1,v + pw[n-d]);
	dfs(ch[x][2],d-1,v + 2 * pw[n-d]);
}
