int main(){
    std::cin.tie(nullptr);
    std::ios::sync_with_stdio(false);
    std::cout << std::setprecision(15) << std::fixed;

    usize n;
    std::cin >> n;
    vec<usize> tpow(n+1, 1);
    for (usize i=0; i<n; i++) {
        tpow.at(i+1) = tpow.at(i) * 3;
    }
    usize N = tpow.back();
    DEBUG(n, N, tpow);

    bool has_s = false;
    vec<vec<mint>> sigma(n);
    for (usize i=0; i<n; i++) {
        sigma.at(i).resize(tpow.at(i));
    }
    DEBUG(matrix_style(sigma));

    // apply-s{{{
    auto apply_s = [&](usize a) {
        for (usize k=0; k<n; k++) {
            usize K = tpow.at(k);
            usize r = a % (3*K);
            if (K <= r && r < 2*K) {
                a += K;
            } else if (2*K <= r) {
                a -= K;
            }
        }
        return a;
    };
    /*}}}*/
    // mul_r_from_the_left{{{
    auto mul_r_from_the_left = [&] {
        for (usize i=n-1; i!=-1; i--) {
            usize I = tpow.at(i);
            usize j = I - 1;
            if (has_s) {
                j = apply_s(j);
            }
            DEBUG(i, j);
            for (usize k=0; k<i; k++) {
                usize K = tpow.at(k);
                usize r = j % (3*K);
                usize val = sigma.at(k).at(r%K).value;
                DEBUG(j,val);
                j -= val * K;
                if (r < val*K) j += 3*K;
                DEBUG(j,val);
            }
            if (has_s) {
                sigma.at(i).at(j)--;
            } else {
                sigma.at(i).at(j)++;
            }
        }
    };
/*}}}*/
    // get-ans{{{
    auto get_ans = [&] {
        vec<usize> ans(N);
        std::iota(ALL(ans), 0u);
        for (usize j=0; j<N; j++) {
            usize& a = ans.at(j);
            for (usize k=n-1; k!=-1; k--) {
                usize K = tpow.at(k);
                usize r = a % (3*K);
                usize val = sigma.at(k).at(r%K).value;
                a += val * K;
                if ((3-val)*K <= r) a -= 3*K;
            }
            if (has_s) {
                a = apply_s(a);
            }
        }
        return ans;
    };
/*}}}*/
