vector<int> dp(map<string, vector<int>>& next, string T, int pow3N){
  auto it = next.find(T);
  if(it!=next.end()){
    return it->second;
  }

  auto rule1 = dp(next, T.substr(0, T.size()/2), pow3N);
  auto rule2 = dp(next, T.substr(T.size()/2), pow3N);

  next[T] = vector<int>(pow3N);
  for(int ii=0; ii<pow3N; ii++){
    next[T][ii] = rule2[rule1[ii]];
  }
  return next[T];
}

int main(){
  int N;
  string T;
  cin >> N >> T;
  int pow3N = pow(3, N);
  map<string, vector<int>> next;
  next["S"] = vector<int>(pow3N);
  next["R"] = vector<int>(pow3N);

  for(int ii=0; ii<pow3N; ii++){
    int tmp=ii;
    int jj=0;
    int aa=1;
    while(tmp){
      int kk = tmp % 3;
      tmp = (tmp - kk) / 3;
      if(kk==1){
        kk=2;
      }else if(kk==2){
        kk=1;
      }
      jj+=kk*aa;
      aa*=3;
    }
    next["S"][ii] = jj;
    next["R"][ii] = ii+1;
  }
  next["R"][pow3N-1] = 0;

  dp(next, T, pow3N);

  for(auto& pos : next[T]){
    cout << pos << " ";
  }
  cout << endl;
  return 0;
}

