#include <bits/stdc++.h>
using namespace std;

void update(int q, int& last, int& d) {
    if (d != 0 and q%2 != last%2) d = 3-d;
    last = q;
}

pair<vector<int>, vector<int>> solve(int n, const string& T) {
    if (n == 0) {
        vector<int> P = {0};
        vector<int> W(T.size(), 0);
        return {P, W}; 
    }
    auto pp = solve(n-1, T);
    const auto& P = pp.first;
    const auto& W = pp.second;
    vector<int> new_W(W.size());
    int pot = P.size();
    vector<int> D(3*pot);
    for (int i = 0; i < 3; i++) for (int x = 0; x < pot; x++) D[i*pot + x] = i;
    vector<int> last(3*pot, 0);
    int q = 0;
    for (int i = 0; i < (int)T.size(); i++) {
        int k = W[i];
        for (int x = k; x < 3*pot; x += pot) {
            update(q, last[x], D[x]);
            if (D[x] == 2) new_W[i] = x;
        }
        if (T[i] == 'S') q++;
        if (T[i] == 'R') {
            for (int x = k; x < 3*pot; x += pot) D[x] = (D[x]+1)%3;
        }
    }

    vector<int> new_P(3*pot);
    for (int i = 0; i < 3*pot; i++) {
        update(q, last[i], D[i]);
        new_P[i] = P[i%pot] + D[i] * pot;
    }

    return {new_P, new_W};
}

int main() {
    int N;
    cin >> N;
    string T;
    cin >> T;
    vector<int> P = solve(N, T).first;
    for (int x: P) cout << x << " ";
    cout << "\n";
}

