struct tree
{
	int ch[3],id,tag;
}a[N];
void build(int d,int x,int v)
{
	a[x].tag=0;
	if(d==n)
	{
		a[x].id=v;
		return;
	}
	for(int i=0;i<3;i++)
	{
		a[x].ch[i]=++cnt;
		build(d+1,a[x].ch[i],v+p[d]*i);
	}
}
void pushdown(int x)
{
	if(a[x].tag)
	{
		for(int i=0;i<3;i++)
		{
			swap(a[a[x].ch[i]].ch[1],a[a[x].ch[i]].ch[2]);
			a[a[x].ch[i]].tag^=1;
		}
		a[x].tag=0;
	}
}
void add(int d,int x)
{
	if(d==n)
		return;
	pushdown(x);
	int b[3];
	for(int i=0;i<3;i++)
		b[i]=a[x].ch[i];
	for(int i=0;i<3;i++)
		a[x].ch[i]=b[(i+2)%3];
	add(d+1,a[x].ch[0]);
}
void out(int d,int x,int v)
{
	if(d==n)
	{
		ans[a[x].id]=v;
		return;
	}
	pushdown(x);
	for(int i=0;i<3;i++)
		out(d+1,a[x].ch[i],v+p[d]*i);
}
