int conv(int inp) {
  int cv=0;
  int fx;
  int t;

  fx = inp;
  for (t=1; t<inp; ) t*=3;
  t/=3;
  cv += sa[inp%t];
  fx /= t;

  for (; fx>0; t*=3) {
    switch(fx % 3) {
    case 1: cv += 2*t; break;
    case 2: cv += t; break;
    }
    fx /= 3;
  }
  return cv;
}

