    const auto& p=pp.fi, w=pp.se;
    VI new_w(sz(w));
    int pot=sz(p);
    VI D(3*pot);
    for(int i=0;i<3;i++)for(int x=0;x<pot;x++)D[i*pot+x]=i;
    VI last(3*pot,0);
    int q=0;
    for(int i=0;i<sz(T);i++){
        int k=w[i];
        for(int x=k;x<3*pot;x+=pot){
            update(q,last[x],D[x]);
            if(D[x]==2)new_w[i]=x;
        }
        if(T[i]=='S')q++;
        if(T[i]=='R'){
            for(int x=k;x<3*pot;x+=pot)D[x]=(D[x]+1)%3;
        }
    }
    VI new_p(3*pot);
    for(int i=0;i<3*pot;i++){
        update(q,last[i],D[i]);
        new_p[i]=p[i%pot]+D[i]*pot;
    }
    return {new_p,new_w};
}

