int get1(int mask, char q) {
    if (q == 'R') {
        ++mask; 
        return mask; 
    }   
    int ptr = 1; 
    int ans = 0; 
    while (mask) {
        int t = mask % 3; 
        mask /= 3; 
        if (t == 2) ans += ptr; 
        if (t == 1) ans += 2 * ptr; 
        ptr *= 3; 
    }
    return ans; 
}

const int L = 12; 
int pref[maxn]; 
int to[maxn]; 
int kek1[N], kek2[N]; 
int res[N]; 
int add = 1; 

int get(int x) {
    vector<int> bts; 
    for (int i = 0; i < L; ++i) {
        bts.pb(x % 3); 
        x /= 3; 
    }
    int left = 0;
    int right = 0; 
    for (int i = 6; i >= 0; --i) {
        left *= 3;
        left += bts[i]; 
    }
    for (int i = L - 1; i >= 7; --i) {
        right *= 3; 
        right += bts[i]; 
    }
    int id = 0; 
    if (left && dp1[left] == -1) {
        return res[left] + add * right;   
    }
    if (left) {    
        id = dp1[left]; 
        if (pref[id] & 1) right = xyz[right];             
        left = 0; 
    }                     
    while (next[id] != -1) {        
        if ((pref[next[id]] - pref[id]) & 1) right = xyz[right]; 
        id = next[id];                                                                                                                                         
    }
    left = to[id]; 
    return left + right * add; 
}

void solve() {
