        res[i] = i; 
        for (int j = 0; j < t; ++j) {
            if (s[j] == 'S') res[i] = xyz[res[i]]; 
            else res[i] = (res[i] + 1) % N; 
        } 
    }
    for (int i = 0; i < t; ++i) {
        pref[i + 1] = pref[i] + (s[i] == 'S'); 
    }

    for (int i = 0; i < N; ++i) dp1[i] = dp2[i] = -1; 
    next[t] = -1;
