int maxn = 531441, B, C;
int n, p[531441], f[531441];
int rev(int a) {
	int r = 0, c = 1;
	while(c < maxn) {
		if(a%3) r +=(3 - (a%3))*c;
		c*=3,a/=3;
	}
	return r;
}

int s = 0;
vector<int> small[729];
int id[531441], lup[531441], u = 0;

void add() {
	s = (s + B - 1)%B;
	for(auto i : small[s]) {
		if(lup[i] != u) {
			if((lup[i]^u)&1) id[i] = f[id[i]];
			lup[i] = u;
		}
		id[i] = (id[i] + 1)%C;
	}
}

void flip() {
	u++;
	for(int i = 0; i < B; i++) {
		if(f[i] <= i) {
			swap(small[(s+i)%B], small[(s+f[i])%B]);
		}
	}
}

