namespace Trie{
    int tot = 1;
    int trie[MAXN][3],val[MAXN],tag[MAXN];

    void Build(int k,int dep,int num){
        if(dep == n){
            val[k] = num;
            return;
        }
        for(int i = 0;i < BASE;i++){
            trie[k][i] = ++tot;
            Build(trie[k][i],dep + 1,num + i * bin[dep]);
        }
    }

    void down(int k){
        if(tag[k]){
            swap(trie[k][1],trie[k][2]);
            tag[k] = 0;
            for(int i = 0;i < BASE;i++)
                tag[trie[k][i]] ^= 1;
        }
    }

    void dfs(int k,int dep,int num){
        if(dep == n){
            ans[val[k]] = num;
            return;
        }
        down(k);
        for(int i = 0;i < BASE;i++)
            dfs(trie[k][i],dep + 1,num + i * bin[dep]);
    }

    void Add(int k,int dep){
        if(dep == n)
            return;
        down(k);
        Add(trie[k][2],dep + 1);
        swap(trie[k][0],trie[k][1]);
        swap(trie[k][0],trie[k][2]);
    }
}

