vector <int> ids[M];

int get(int t){
	int ret = 0, tmp = 1;
	while(t){
		int r = t % 3;
		t /= 3;

		if(r)	r ^= 3;
		ret += tmp * r;
		tmp *= 3;
	}

	return ret;
}

void swap_all(){
	for(int i = 0; i < p; ++i)
		cur[i] = nxt[cur[i]];

	for(int i = 0; i < q; ++i){
		up_val[i] = nxt[up_val[i]];
		up_rv[up_val[i]] = i;
	}
}

void add(){
	for(int i = 0; i < p; ++i)
		if(cur[i] + 1 < p)
			++cur[i];
		else{
			cur[i] = 0;
			for(auto t: ids[i]){
				int me = up_val[up[t]];
				if(me + 1 < q)
					++me;
				else
					me = 0;
				up[t] = up_rv[me];
			}
		}
}

