	all = 1;
	for(int i = 1; i <= n; ++i)
		all *= 3;

	p = 1, q = 1;
	for(int i = 1; i <= (n + 1) / 2; ++i)
		p *= 3;

	for(int i = (n + 1) / 2 + 1; i <= n; ++i)
		q *= 3;

	for(int i = 0; i < p; ++i)
		nxt[i] = get(i);

	for(int i = 0; i < all; ++i){
		down[i] = i % p;
		up[i] = i / p;
		ids[down[i]].push_back(i);
	}

	for(int i = 0; i < p; ++i)
		cur[i] = i;
	for(int i = 0; i < q; ++i)
		up_val[i] = up_rv[i] = i;

	scanf("%s", instr + 1);
	int l = strlen(instr + 1);

	for(int v = 1; v <= l; ++v){
		if(instr[v] == 'R')
			add();
