int get_sum(int l, int r) {
    if (!l) {
        return sum[r];
    }
    return sum[r] - sum[l - 1];
}

void get_all_prec() {
    pw = 1;
    for (int i = 0; i < n; ++i) {
        pw *= 3;
    }
    for (int x = 0; x < pw; ++x) {
        vector<int> v;
        int cur = x;
        for (int i = 0; i < n; ++i) {
            v.push_back(cur % 3);
            cur /= 3;
        }
        reverse(v.begin(), v.end());
        int to = 0;
        for (int i = 0; i < n; ++i) {
            int x = (v[i] * 2) % 3;
            to = to * 3 + x;
        }
        prec[x] = to;
    }
