        mt19937 gen;
        n = 12;
        for (int i = 0; i < 200000; ++i) {
            s += "SR"[gen() % 2];
        }
    } else {
        scanf("%d", &n);
        s = read();
    }
    get_all_prec();
    const int half = min(max_half, pw);
    for (int x = 0; x < half; ++x) {
        nxt[s.length()][x] = -1 - x;
    }
    for (int i = 0; i < s.length(); ++i) {
        sum[i] = s[i] == 'S';
        if (i) {
            sum[i] += sum[i - 1];
        }
    }
    int tot = 0;
    for (int i = s.length() - 1; i >= 0; --i) {
        for (int x = 0; x < half; ++x) {
            int to = prec[x];
            if (s[i] == 'R') {
                to = x + 1;
            }
            if (to == half) {
                nxt[i][x] = i;
            } else {
                nxt[i][x] = nxt[i + 1][to];
            }
            tot += nxt[i][x];
            //cout << nxt[i][x] << " ";
        }
        //cout << endl;
    }
    int mx = pw / half;
    for (int ismall = 0; ismall < half; ++ismall) {
        for (int big = 0; big < mx; ++big) {
            res[big] = big;
        }
        int small = ismall;
        int pos = 0;
        while (pos < s.length()) {
            //cout << pos << ": " << big << " " << small << endl;
            if (nxt[pos][small] < 0) {
                small = -1 - nxt[pos][small];
                if (get_sum(pos, s.length() - 1) % 2) {
                    for (int big = 0; big < mx; ++big) {
                        res[big] = prec[res[big]];
                    }
                }
                pos = s.length();
            } else {
                int to = nxt[pos][small];
                if (get_sum(pos, to) % 2) {
                    for (int big = 0; big < mx; ++big) {
                        res[big] = prec[res[big]] + 1;
                        if (res[big] == mx) {
                            res[big] = 0;
                        }
                    }
                }
                small = 0;
                pos = to + 1;
            }
        }
        for (int big = 0; big < mx; ++big) {
            ans[big * half + ismall] = res[big] * half + small;
        }
    }
    for (int i = 0; i < pw; ++i) {
        printf("%d ", ans[i]);
    }
    return 0;
}

