string reduce (const string &s)
{
	string ans;
	for (char ch : s)
	{
		if (ans.empty() || ans.back() != ch || ch != 'S')
			ans += ch;
		else
			ans.pop_back();
	}
	return ans;
}

vector<int> work (const int n, const string &s)
{
	vector<int> who_moves(3);
	vector<string> carry(3);

	for (int i = 0; i < 3; i++)
	{
		int cur = i;
		for (const char ch : s)
		{
			if (ch == 'S')
			{
				carry[i] += ch;
				if (cur != 0)
					cur = 3 - cur;
			}

			if (ch == 'R')
			{
				cur++;
				if (cur == 3)
				{
					carry[i] += ch;
					cur = 0;
				}
			}
		}

		who_moves[i] = cur;
	}

	if (n == 1)
		return who_moves;

	for (int i = 0; i < 3; i++)
		carry[i] = reduce(carry[i]);

	vector<vector<int>> old_anses(3);
	for (int i = 0; i < 3; i++)
		old_anses[i] = work(n - 1, carry[i]);
	for (int i = 0; i < 3; i++)
		assert(old_anses[i].size() == old_anses[0].size());

	vector<int> ans(3 * old_anses[0].size());
	for (int i = 0; i < 3; i++)
		for (int j = 0; j < int(old_anses[i].size()); j++)
			ans[3 * j + i] = 3 * old_anses[i][j] + who_moves[i];

	return ans;
}

