struct Tree {
    int value;
    struct Tree * l;
    struct Tree * m;
    struct Tree * r;

    Tree(int _value) : value(_value), l(NULL), m(NULL), r(NULL) {};
};

vector<int> pow3s(13, 0);

int pow3(int n) {
    if (pow3s[n] == 0) {
        pow3s[0] = 1;
        for (int i = 1; i <= 12; ++i) {
            pow3s[i] = 3 * pow3s[i-1];
        }
    }
    return pow3s[n];
}

struct Tree * constructTree(int n, int offset) {
    if (n == 0) {
        return new Tree(offset);
    }
    auto cur = new Tree(offset);

    cur->l = constructTree(n-1, offset);
    cur->m = constructTree(n-1, offset + pow3(n-1));
    cur->r = constructTree(n-1, offset + pow3(n-1) * 2);
    return cur;
}

void placeTree(Tree * t, vector<int> & lst) {
    if (t->l == NULL) {
        lst.push_back(t->value);
        return;
    }
    placeTree(t->l, lst);
    placeTree(t->m, lst);
    placeTree(t->r, lst);
}

void rotate(Tree * t) {
    if (t->l == NULL) return;

    rotate(t->l);
    rotate(t->m);
    rotate(t->r);

    auto tmp = t->m;
    t->m = t->r;
    t->r = tmp;
}

int shift(Tree * t, int v) {
    if (t->l == NULL) {
        auto tmp = t->value;
        t->value = v;
        return tmp;
    }

    int u;
    u = shift(t->l, v);
    u = shift(t->m, u);
    u = shift(t->r, u);
    return u;
}

void put(Tree * t, int v) {
    if (t->l == NULL) {
        t->value = v;
        return;
    } else {
        put(t->l, v);
    }
}

void printTree(Tree * t) {
    if (t->l == NULL) {
        cerr << t->value << " ";
    } else {
        printTree(t->l);
        printTree(t->m);
        printTree(t->r);
    }
}

int main() {
    int n;
    cin >> n;
    Tree * t = constructTree(n, 0);
    string s;
    cin >> s;
    //printTree(t); cerr << endl;
    for (auto c : s) {
        if (c == 'S') {
            rotate(t);
        } else {
            int v = shift(t, 0);
            put(t, v);
        }
        //printTree(t); cerr << endl;
    }
    //printTree(t); cerr << endl;
    vector<int> v;
    placeTree(t, v);
    vector<int> u(pow3(n));
    for (ll i = 0; i < pow3(n); ++i) {
        u[v[i]] = i;
    }
    for (auto & p: u) {
        cout << p << " ";
    }
    cout << endl;
}

