vector<ll> k_3;

int turn(int x){
  static vector<int> dp(400000,-1);
  if(x==0)return 0;
  int &it = dp[x];
  if(it>=0)return it;
  return it = turn(x/3)*3 + ((x%3==0)?0:(3-x%3));
}

int main(){
  int n = in();
  int n2 = n/2;
  vector<int> line = inputs();
  k_3.emplace_back(1);
  rep(i,20){
    k_3.emplace_back(k_3.back()*3);
  }
  vvector<int> moves(k_3[n2]);
  vector<int> turn_sum(line.size()+1,0);
  rep(i,line.size()){
    turn_sum[i+1] = turn_sum[i] + line[i];
  }

  rep(i,k_3[n2]){
    vector<int> &move = moves[i];
    move.emplace_back(i);
    int current = i;
    foreach(j,line){
      if(j){
        current = current / k_3[n2] * k_3[n2] + turn(current%k_3[n2]) ;
      }else{
        ++current;
      }
      move.emplace_back(current);
    }
  }

  vector<int> ans(k_3[n],-1);
  rep(i,k_3[n]){
    int current = i;
    int index = current % k_3[n2];
    int p = 0;
    vector<int> &move = moves[index];
    while(p<move.size()){
      int np = lower_bound(all(move),(move[p]+k_3[n2])/k_3[n2]*k_3[n2])-move.begin();
      if(np==move.size()){
        if((turn_sum.back()-turn_sum[p])&1){
          int upper = current / k_3[n2];
          current = turn(upper) * k_3[n2] + current % k_3[n2];
        }
        current += move.back() - move[p];
        current %= k_3[n];
        break;
      }
      if((turn_sum[np]-turn_sum[p])&1){
        int upper = current / k_3[n2];
        current = turn(upper) * k_3[n2] + current % k_3[n2];
      }
      current = (current + k_3[n2]) / k_3[n2] * k_3[n2];
      current %= k_3[n];
      p = np;
    }
    ans[i] = current;
  }

  foreach(i,ans){
