void ins(int x)
{
	int now = w, X = x;
	for(int i = 0; i < n - 1; i++)
	{
		if(!ch[now][x % 3]) ch[now][x % 3] = ++tot;
		now = ch[now][x % 3];
		x /= 3;
	}
	ch[now][x] = X;
}

void pushdown(int x)
{
	if(tag[x])
	{
		std::swap(ch[x][1], ch[x][2]);
		tag[ch[x][0]] ^= 1;
		tag[ch[x][1]] ^= 1;
		tag[ch[x][2]] ^= 1;
		tag[x] = 0;
	}
}

int get(int x)
{
	int now = w;
	for(int i = 0; i < n; i++)
	{
		pushdown(now);
		now = ch[now][x % 3];
		x /= 3;
	}
	return now;
}

