struct tree{
  int s[3],id,rev;
}tr[N+9];
int cn;

void Build(){tr[cn=1].id=-1;}

void Update_rev(int k){swap(tr[k].s[1],tr[k].s[2]);tr[k].rev^=1;}

void Pushdown(int k){
  if (!tr[k].rev) return;
  for (int i=0;i<3;++i) Update_rev(tr[k].s[i]);
  tr[k].rev=0;
}

void Insert(int x){
  int k=1;
  for (int i=0,now=x;i<n;++i,now/=3){
	int &t=tr[k].s[now%3];
	tr[k=t=t?t:++cn].id=-1;
  }
  tr[k].id=x;
}

void Change_add(int k){
  if (tr[k].id>=0) return;
  int t=tr[k].s[2];
  Pushdown(k);
  for (int i=2;i>=1;--i) tr[k].s[i]=tr[k].s[i-1];
  Change_add(tr[k].s[0]=t);
}

int ans[N+9];

void Dfs_ans(int k,int v,int pw){
  if (tr[k].id>=0) {ans[tr[k].id]=v;return;}
  Pushdown(k);
  for (int i=0;i<3;++i) Dfs_ans(tr[k].s[i],v+i*pw,pw*3);
}

