        x /= 3;
        k++;
    }

    return res;
}

ll l3(ll x) {
    ll i, j, k, p3;
    ll res;
    k = 0; p3 = 1;
    res = 0;
    while (x != 0) {
        j = x % 3;
        x /= 3;
        if (j == 1) {
            j = 2;
        } else if (j == 2) {
            j = 1;
        }
        res += (p3 * j);
        k++; p3 *= 3;
    }

    return res;
}

