typedef class TrieNode {
	public:
		TrieNode *ch[3];
		bool swp12;
		int id;

		void swap12() {
			swp12 ^= 1;
			swap(ch[1], ch[2]);
		}
		bool leaf() {
			return !ch[0];
		}
		void push_down() {
			if (swp12) {
				ch[0]->swap12();
				ch[1]->swap12();
				ch[2]->swap12();
				swp12 = false;
			}
		}

		void get_ans(int base, int v) {
			if (leaf()) {
				ans[id] = v;
				return;
			}
			push_down();
			ch[0]->get_ans(base * 3, v);
			ch[1]->get_ans(base * 3, v += base);
			ch[2]->get_ans(base * 3, v += base);
		}
} TrieNode;

TrieNode pool[1000000];
