TrieNode *newnode() {
	return _top++;
}

typedef class Trie {
	public:
		TrieNode* rt;

		void build(TrieNode*& p, int r, int base, int v) {
			p = newnode();
			if (r == 0) {
				p->id = v;
				return;
			}
			build(p->ch[0], r - 1, base * 3, v);
			build(p->ch[1], r - 1, base * 3, v += base);
			build(p->ch[2], r - 1, base * 3, v += base);
		}
		void build(int n) {
			build(rt, n, 1, 0);
		}

		void swap12() {
			rt->swap12();
		}

		void update(TrieNode*& p) {
			if (p->leaf()) {
				return;
			}
			p->push_down();
			swap(p->ch[0], p->ch[1]);
			swap(p->ch[0], p->ch[2]);
			update(p->ch[0]);
		}
		void update() {
			update(rt);
		}

		void get_ans() {
			rt->get_ans(1, 0);
		}
} Trie;

