        }
        DEBUG(has_s);
        DEBUG(matrix_style(sigma));
        auto ans = get_ans(); DEBUG(ans);
        DEBUG();
    }

    auto ans = get_ans();
    for (usize i=0; i<N; i++) {
        std::cout << (i?" ":"") << ans.at(i);
    }
    std::cout << '\n';
}

