int main()
{
	scanf("%d%s",&N,s + 1);
	m = strlen(s + 1);
	for (int i = 1;i <= m;i++)
	{
		sum[i] = sum[i - 1] + (s[i] == 'S');
		if (s[i] == 'R')
			add[0][0].push_back(i);
	}
	int v = 1;
	for (int i = 1;i <= N;i++,v *= 3)
		for (int j = 0;j < 3;j++)
			for (int k = 0;k < v;k++)
			{
				to[i & 1][k + v * j] = to[~i & 1][k];
				add[i & 1][k + v * j].clear();
				int cur = j,lst = 0;
				for (int l = 0;l < add[~i & 1][k].size();l++)
				{
					int p = add[~i & 1][k][l];
					if ((sum[p] - sum[lst]) & 1)
					{
						cur = (cur == 1 || cur == 2 ? 3 - cur : cur);
						lst = p;
					}
					cur++;
					if (cur == 3)
					{
						add[i & 1][k + v * j].push_back(p);
						cur = 0;
					}
				}
				if ((sum[m] - sum[lst]) & 1)
					cur = (cur == 1 || cur == 2 ? 3 - cur : cur);
				to[i & 1][k + v * j] += v * cur;
			}
	for (int i = 0;i < v;i++)
		printf("%d ",to[N & 1][i]);
	printf("\n");
	return 0;
}

